package com.picpay.desafio.android.repository

import com.picpay.desafio.android.local.database.UsersDao
import com.picpay.desafio.android.local.modal.UsersDataCache
import com.picpay.desafio.android.local.source.UsersCacheDataSource
import com.picpay.desafio.android.local.source.UsersCacheDataSourceImpl
import com.picpay.desafio.android.remote.api.UserApi
import com.picpay.desafio.android.remote.model.UserData
import com.picpay.desafio.android.remote.repository.UsersRepositoryImpl
import com.picpay.desafio.android.remote.source.RemoteDataSource
import com.picpay.desafio.android.remote.source.RemoteDataSourceImpl
import io.mockk.MockKAnnotations
import io.mockk.coEvery
import io.mockk.every
import io.mockk.mockk
import kotlinx.coroutines.flow.collect
import kotlinx.coroutines.flow.flow
import kotlinx.coroutines.runBlocking
import org.junit.Assert
import org.junit.Before
import org.junit.Test
import org.junit.runner.RunWith
import org.junit.runners.JUnit4

@RunWith(JUnit4::class)
class UsersRepositoryTest {

    private lateinit var usersRepository: UsersRepository

    private lateinit var usersCacheDataSource: UsersCacheDataSource

    private lateinit var remoteDataSource: RemoteDataSource

    @Before
    fun setup() {
        MockKAnnotations.init(this, relaxed = true)
    }

    @Test
    fun `test get users from api`() = runBlocking {
        //ARRANGE
        val mockApiService = mockk<UserApi>(relaxed = true)
        coEvery { mockApiService.getUsers() } returns USERS_REMOTE_MOCK_RESULT
        val mockCacheService = mockk<UsersDao>(relaxed = true)
        every { mockCacheService.getUsers() } returns flow { EMPTY }

        remoteDataSource = RemoteDataSourceImpl(mockApiService)
        usersCacheDataSource = UsersCacheDataSourceImpl(mockCacheService)

        // ACT
        usersRepository = UsersRepositoryImpl(
            remoteDataSource = remoteDataSource,
            usersCacheDataSource = usersCacheDataSource
        )

        //ASSERT
        usersRepository.getUsers().collect {
            Assert.assertEquals(USERS_REMOTE_MOCK_RESULT, usersRepository)
        }
    }

    @Test
    fun `test get users from cache`() = runBlocking {
        //ARRANGE
        val mockApiService = mockk<UserApi>(relaxed = true)
        coEvery { mockApiService.getUsers() } returns listOf()
        val mockCacheService = mockk<UsersDao>(relaxed = true)
        every { mockCacheService.getUsers() } returns flow {
            USERS_CACHE_MOCK_RESULT
        }

        remoteDataSource = RemoteDataSourceImpl(mockApiService)
        usersCacheDataSource = UsersCacheDataSourceImpl(mockCacheService)

        // ACT
        usersRepository = UsersRepositoryImpl(
            remoteDataSource = remoteDataSource,
            usersCacheDataSource = usersCacheDataSource
        )

        //ASSERT
        usersRepository.getUsers().collect {
            Assert.assertEquals(USERS_CACHE_MOCK_RESULT, usersRepository)
        }
    }

    companion object {
        val USERS_CACHE_MOCK_RESULT = listOf(
            UsersDataCache(
                1,
                "data:image/png;base64,",
                "Test One",
                "Test 1"
            ),
            UsersDataCache(
                2,
                "data:image/png;base64,",
                "Test Two",
                "Teste 2"
            )
        )
        val USERS_REMOTE_MOCK_RESULT = listOf(
            UserData(
                1,
                "data:image/png;base64,",
                "Test One",
                "Test 1"
            ),
            UserData(
                2,
                "data:image/png;base64,",
                "Test Two",
                "Teste 2"
            )
        )
        const val EMPTY = ""
    }
}